import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Card.css';

const AddUserForm = () => {
  const [newFirstName, setNewFirstName] = useState('');
  const [newLastName, setNewLastName] = useState('');
  const [newEmailAddress, setNewEmailAddress] = useState('');
  const [newPhoneNumber, setNewPhoneNumber] = useState('');
  const [newUserSpecialities, setNewUserSpecialities] = useState('');
  const [data, setData] = useState([]);

  useEffect(() => {
    axios.get('http://127.0.0.1:5000/add_user')
      .then(response => {
        setData(response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const handleAddUser = () => {
    const newUser = {
      first_name: newFirstName,
      last_name: newLastName,
      email_address: newEmailAddress,
      phone_number: newPhoneNumber,
      specialities: newUserSpecialities
    };
      axios.post('http://127.0.0.1:5000/add_user', newUser)
      .then(response => {
        console.log('User added:', response.data);
        setNewFirstName('');
        setNewLastName('');
        setNewEmailAddress('');
        setNewPhoneNumber('');
        setNewUserSpecialities('');
      })
      .catch(error => {
        console.error('Error adding user:', error);
      });
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Prénom"
        value={newFirstName}
        onChange={e => setNewFirstName(e.target.value)}
        className="input-field"
      />
      <input
        type="text"
        placeholder="Nom de famille"
        value={newLastName}
        onChange={e => setNewLastName(e.target.value)}
        className="input-field"
      />
      <input
        type="email"
        placeholder="Adresse e-mail"
        value={newEmailAddress}
        onChange={e => setNewEmailAddress(e.target.value)}
        className="input-field"
      />
      <input
        type="tel"
        placeholder="Numéro de téléphone"
        value={newPhoneNumber}
        onChange={e => setNewPhoneNumber(e.target.value)}
        className="input-field"
      />
      <input
        type="text"
        placeholder="Spécialités"
        value={newUserSpecialities}
        onChange={e => setNewUserSpecialities(e.target.value)}
        className="input-field"
      />
        <button className="add-user-button" onClick={handleAddUser}>
          Add User
        </button>
      </div>
   
  );
};

export default AddUserForm;
